import { useState } from "react";
import Logo from "./Logo";
import Form from "./Form";
// const initialItems = [
//   { id: 1, description: "Passports", quantity: 2, packed: false },
//   { id: 2, description: "Socks", quantity: 12, packed: true },
//   { id: 3, description: "Charger", quantity: 1, packed: false },
// ];

export default function App() {
  const [items, setItems] = useState([]);
  function handleAddItems(item) {
    // console.log(item);
    setItems((items) => [...items, item]);
    // console.log(items, item);
  }
  function handleDeleteItem(Itemid) {
    // console.log(Itemid);
    setItems((item) => items.filter((item) => item.id !== Itemid));
  }
  function handleToggleItem(id) {
    setItems((items) =>
      items.map((item) =>
        item.id === id ? { ...item, packed: !item.packed } : item
      )
    );
  }
  function handleClear() {
    const confirmed = window.confirm("do you want to delete all items");
    console.log(confirmed);
    if (confirmed) setItems([]);
  }
  return (
    <div className="app">
      <Logo />
      <Form onAddItems={handleAddItems} />
      <PackingList
        items={items}
        handleAddItems={handleAddItems}
        handleDeleteItem={handleDeleteItem}
        handleToggleItem={handleToggleItem}
        handleClear={handleClear}
      />
      <Stat items={items} />
    </div>
  );
}

function Item({ item, handleDeleteItem, handleToggleItem }) {
  return (
    <li>
      <input
        type="checkbox"
        onChange={() => {
          handleToggleItem(item.id);
        }}
        value={item.packed}
      />
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.quantity} {item.description}
      </span>
      <button onClick={() => handleDeleteItem(item.id)}>‚ùå</button>
    </li>
  );
}
function Stat({ items }) {
  if (!items.length)
    return (
      <p className="stats">
        <em>Start adding some item to your packing list .</em>
      </p>
    );
  const numItems = items.length;
  const numPacked = items.filter((item) => item.packed).length;
  const percentage = Math.round((numPacked / numItems) * 100);
  return (
    <footer className="stats">
      <em>
        {percentage === 100
          ? "you got everything , ready to go‚úàÔ∏è"
          : ` üíºYou have ${numItems} item in your list, you already packed ${numPacked}(
        ${percentage}%)`}
      </em>
    </footer>
  );
}
